package com.ly666.mapper;import com.ly666.entity.Article;import org.apache.ibatis.annotations.Param;import org.springframework.stereotype.Repository;import java.util.List;/** * @author ：luoyi * @date ：Created in 2019/3/4 21:46 * @desc : {} */@Repository("ArticleMapper")public interface ArticleMapper {	/*	* 获取全部文章数据	* */	List<Article> getAllArticle();	/*	* 获取文章记录	* */	Integer articleCount();	/*	* 添加文章	* */	void addArticle(Article article);	/*	* 获取指定文章数据	* */	Article getArticle(Integer article_id);	/*	* 模糊查询文章数据	* */	List<Article> getLikeArticles(@Param("str") String str);	/*	* 模糊查询文章记录	* */	Integer likeArticleCount(@Param("str") String str);	/*	* 更新文章数据	* */	void updateArticle(Article article);	/*	* 删除指定文章	* */	void deleteArticle(Integer article_id);	/*	* 获取大类别下所有文章	* */	List<Article> getBigCategoryArticle(@Param("parentid") Integer parentid);	/*	 * 获取大类别下所有文章记录	 * */	Integer getBigCategoryArticleCount();	/*	* 获取小类别下所有文章	* */	List<Article> getSmallCategoryArticle(@Param("cid") Integer cid);	/*	 * 获取小类别下所有文章记录	 * */	Integer getSmallCategoryArticleCount();}