package com.ly666.service;import com.github.pagehelper.PageInfo;import com.ly666.entity.Article;/** * @author ：luoyi * @date ：Created in 2019/3/4 21:50 * @desc : {} */public interface ArticleService {	/*	* 获取分页数据	* */	PageInfo<Article> getPageArticle(Integer pageNum, Integer pageSize);	/*	* 添加文章	* */	boolean addArticle(Article article);	/*	* 更新文章	* */	boolean updateArticle(Article article);	/*	* 获取指定文章数据	* */	Article getArticle(Integer article_id);	/*	* 删除文章	* */	void deleteArticle(Integer article_id);	/*	 * 接受文章数据并检验,如果数据都合法则可进行文章的添加与更新操作	 * */	boolean checkArticleData(Article article);	/*	* 模糊查询	* */	PageInfo<Article> getLikeArticles(Integer pageNum, Integer pageSize, String str);	/*	 * 获取大类别下所有文章并分页展示	 * */	PageInfo<Article> getBigCategoryArticle(Integer pageNum, Integer pageSize, Integer parentid);	/*	 * 获取小类别下所有文章并分页展示	 * */	PageInfo<Article> getSmallCategoryArticle(Integer pageNum, Integer pageSize, Integer cid);}