package com.ly666.service;import com.github.pagehelper.PageInfo;import com.ly666.entity.Category;import java.util.List;/** * @author ：luoyi * @date ：Created in 2019/3/4 10:24 * @desc : {} */public interface CategoryService {	/**	 * 添加分类	 * @param category     获取分类对象	 * @throws Exception   当添加的对象不符合要求将抛出相应的异常信息	 */	void addCategory(Category category) throws Exception;	/**	 * 获取所有分类数据并分页展示	 * @param pageNum     当前页码	 * @param pageSize    一页显示多少条数据	 * @return            返回一个包装相应实体数据的分页信息对象	 */	PageInfo<Category> queryAllCategory(Integer pageNum, Integer pageSize);	/**	 * 获取指定cid的分类数据	 * @param cid   分类对象的唯一标识	 * @return      返回此标识的分类对象	 */	Category getCategory(Integer cid);	/**	 * 更新类数据	 * @param category      一个分类对象	 * @throws Exception    当更新的数据出现问题将抛出异常	 */	void update(Category category) throws Exception;	/**	 * 删除指定cid的分类数据	 * @param cid    删除信息的唯一标识	 */	void delete(Integer cid);	/**	 * 根据大分类获取相应的小分类数据	 * @param parentid    parentid表示子分类属于某个大分类的cid	 * @return            返回一个list集合数据	 */	List<Category> getParentCategory(Integer parentid);	/*	 * 检验接收的分类数据	 * */	Boolean checkCategory(Category category) throws Exception;	/*	* 获取指定分类名的数据	* */	Category getCnameCategory(String cname);}