package com.ly666.service.impl;import com.ly666.entity.Admin;import com.ly666.mapper.UserMapper;import com.ly666.service.LoginService;import org.springframework.stereotype.Service;import javax.annotation.Resource;/** * @author ：luoyi * @date ：Created in 2019/3/3 22:45 * @desc : {} */@Service("LoginService")public class UserLoginImpl implements LoginService {	@Resource(name = "UserMapper")	private UserMapper userMapper;	@Override	public Admin login(final String admin_name, final String admin_pwd){		return userMapper.login(admin_name, admin_pwd);	}}