package com.ly666.util.filter;import com.ly666.entity.Admin;import javax.servlet.*;import javax.servlet.annotation.WebFilter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * @author ：luoyi * @date ：Created in 2019/3/11 8:27 * @desc : {} */@WebFilter("/bk_admin/*")public class AdminFilter implements Filter {	@Override	public void init(final FilterConfig filterConfig) throws ServletException {	}	@Override	public void doFilter(final ServletRequest servletRequest, final ServletResponse servletResponse, final FilterChain filterChain) throws IOException, ServletException {		HttpServletRequest req = (HttpServletRequest) servletRequest;		HttpServletResponse resp = (HttpServletResponse) servletResponse;		Admin admin = (Admin)req.getSession().getAttribute("admin");		if(admin ==null){			resp.sendRedirect(req.getContextPath()+"/index.jsp");		}else {			filterChain.doFilter(servletRequest, servletResponse);		}	}	@Override	public void destroy() {	}}