package com.ly666.util.interceptor;import com.ly666.entity.Admin;import org.springframework.web.servlet.HandlerInterceptor;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * @author ：luoyi * @date ：Created in 2019/3/10 21:00 * @desc : {拦截所有未经登入就访问后台的url} */public class BkAdminInterceptor implements HandlerInterceptor {	@Override	public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {		Admin admin = (Admin)request.getSession().getAttribute("admin");		if(admin !=null) {			return true;		}		else {			response.sendRedirect(request.getContextPath()+"/index.jsp");			return false;		}	}}