package com.ly666.web.jsonPage;import com.github.pagehelper.PageInfo;import com.ly666.entity.Article;import com.ly666.entity.Category;import com.ly666.service.ArticleService;import com.ly666.service.CategoryService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import javax.annotation.Resource;import java.util.List;/** * @author ：luoyi * @date ：Created in 2019/3/8 8:10 * @desc : {首页文章展示} */@Controller@RequestMapping("jsonCategoryArticle")public class JsonCategoryArticle {	@Resource(name = "ArticelServiceImpl")	ArticleService articleService;	@Resource(name = "CategoryServiceImpl")	CategoryService categoryService;	@RequestMapping("/jsonArticle/{pageNum}")	@ResponseBody	public PageInfo<Article> homeArticle(@PathVariable Integer pageNum, Integer parentid, Integer cid) {		Integer pageSize = 9;		if (parentid != null)			return articleService.getBigCategoryArticle(pageNum, pageSize, parentid);		else if (cid != null) {			return articleService.getSmallCategoryArticle(pageNum, pageSize, cid);		}		else			return articleService.getPageArticle(pageNum, pageSize);	}	@RequestMapping("jsonCurrentArticle/{article_id}")	@ResponseBody	public Article homeGetArticle(@PathVariable Integer article_id) {		return articleService.getArticle(article_id);	}	@RequestMapping("jsonParentCategory/{parentid}")	@ResponseBody	public List<Category> getParentCategory(@PathVariable Integer parentid) {		return categoryService.getParentCategory(parentid);	}	@RequestMapping("smallCategoryArticle/{cid}")	@ResponseBody	public PageInfo<Article> smallCategoryArticle(@PathVariable Integer cid, Integer pageNum) {		Integer pageSize = 9;		return articleService.getSmallCategoryArticle(pageNum, pageSize, cid);	}}